                //val graphPerT:Graph[Array[Double], Double] = Graph(vertices, edges)
        //        println(s)
        //    }
        //}
        val vertices = s._1.toSeq
        val edges = s._2.toSeq
        //val graphPerT:Graph[Array[Double], Double] = Graph(vertices, edges)

        val Adj_A = build_Adj(vertices, edges)
        val Deg_A = build_Deg(vertices, edges)
        Deg_A
    }

    def build_Adj(verticeseq: Seq[(Long, Array[Double])], edgeseq: Seq[((Long, Long), Double)]) = {
        var Adj_A = DenseMatrix.zeros[Double](verticeseq.size,verticeseq.size)
        for (edge <- edgeseq){
            Adj_A(verticeseq.indexOf(verticeseq.find(_._1 == edge._1._1).get),verticeseq.indexOf(verticeseq.find(_._1 == edge._1._2).get)) = 1
            Adj_A(verticeseq.indexOf(verticeseq.find(_._1 == edge._1._2).get),verticeseq.indexOf(verticeseq.find(_._1 == edge._1._1).get)) = 1

        }
        Adj_A
    }

    def build_Deg(verticeseq: Seq[(Long, Array[Double])], edgeseq: Seq[((Long, Long), Double)]) = {
        var Deg_A = DenseMatrix.zeros[Double](verticeseq.size,verticeseq.size)
        for (v <- verticeseq){
            Deg_A(verticeseq.indexOf(v),verticeseq.indexOf(v)) = edgeseq.count(x => x._1._1 == v._1 || x._1._2 == v._1)
        }
        Deg_A
    }

    def Graphtxt(s: Tuple2[Iterable[(Long, Array[Double])],Iterable[((Long, Long), Double)]])= {
        val vertices = s._1.map(s => ("Vetrex",s._1,s._2.mkString))
        val edges = s._2.map(s=> ("edge",s._1,s._2))

        (vertices, edges)
    }
}

